((e) => {
  e.getCountryFlag = async function (e, r) {
    var o,
      e = await (async (e) => {
        if (e)
          try {
            var r = await (
              await fetch(
                "http://127.0.0.1:8787/api/getFlag?country=" +
                  encodeURIComponent(e)
              )
            ).json();
            return r.success && r.secureUrl
              ? r.secureUrl
              : (console.error("Failed to fetch flag:", r.message), null);
          } catch (e) {
            return console.error("Error fetching flag:", e), null;
          }
        else console.error("Country code is required.");
      })(e);
    e &&
      ((o = document.getElementById(r))
        ? (o.src = e)
        : console.error(`Image element with ID '${r}' not found.`));
  };
})(window);
